mapper.insert:
insert into qna_board(
	title, writer, content, email
) values (
	#{title}, #{writer}, #{content}, #{email}
)

mapper.update:
update qna_board 
   set title = #{title}, 
   	   content = #{content}
 where no = #{no}

mapper.select:
select id, no, writer, content
  from qna_answer
  where no = #{no}

mapper.insert:
insert into qna_answer(
	no, writer, content
) values (
	#{no}, #{writer}, #{content}
)
